import React, { Component } from 'react';
import { withTheme } from '@material-ui/core';
// import '../../sass/components/_card.scss';

class Card extends Component {
  render() {
    const cardCaptionStyling = {
      //   backgroundImage:
      //     'linear-gradient(rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.8))',
      background: 'linear-gradient(180deg,transparent,rgba(0,0,0,0.8))',
      position: 'absolute',
      bottom: '0',
      color: 'white',
      padding: '0 1rem',
      width: '100%'
    };

    const cardImageStyling = {
      backgroundPosition: '50%',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      backgroundImage: `url(${this.props.data.img})`,
      visibility: 'visible',
      width: '100%',
      height: '28.7rem' //temporary
    };

    return (
      <div className='Card'>
        <div className='card-img' style={cardImageStyling} alt=''>
          <div className='flex-col'>
            <div className='flex '>
              <div className='card-caption' style={cardCaptionStyling}>
                <div className='card-listing-left-wrapper'>
                  <div>{this.props.data.price}</div>
                  <div>{this.props.data.address}</div>
                  <div>{this.props.data.neighborhood}</div>
                </div>
                <div className='card-listing-right-wrapper'>
                  <div>
                    {this.props.data.beds} <span>Beds</span>
                  </div>
                  <div>
                    {this.props.data.baths} <span>Baths</span>
                  </div>
                  <div>
                    {this.props.data.sq} <span>Sq. Ft.</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Card;
