import React, { Component } from 'react';
import { withTheme } from '@material-ui/core';
import classes from '../../sass/components/_card.scss';

class Card extends Component {
  render() {
    const cardCaptionStyling = {
      //   backgroundImage:
      //     'linear-gradient(rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.8))',
      background: 'linear-gradient(180deg,transparent,rgba(0,0,0,0.8))',
      position: 'absolute',
      bottom: '0',
      color: 'white',
      padding: '1rem 0',
      width: '100%'
    };

    const cardImageStyling = {
      backgroundPosition: '50%',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      backgroundImage: `url(${this.props.data.img})`,
      visibility: 'visible',
      width: '100%',
      height: '28.7rem' //temporary
    };

    return (
      <div className='Card'>
        <div className='card-img' style={cardImageStyling} alt=''>
          <div className='card-caption' style={cardCaptionStyling}>
            <div className={classes.flex}>
              <div className='card-listing-left-wrapper'>
                <span>{this.props.data.price}</span>
                <span>{this.props.data.address}</span>
                <span>{this.props.data.neighborhood}</span>
              </div>
              <div className='card-listing-right-wrapper'>
                <span>{this.props.data.beds}</span>
                <span>{this.props.data.baths}</span>
                <span>{this.props.data.sq}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Card;
