import React, { Component } from 'react';
import Navbar from '../components/UI/navbar';
import Button from '../components/UI/button';

class Header extends Component {
  state = { isForSale: true };
  toogleListingType = () => {
    this.setState({ isForSale: !this.state.isForSale });
  };
  render() {
    return (
      <header className='header'>
        <div className='header__nav-box'>
          <Navbar />
          <div className='row'>
            <div className='header__content'>
              <h1 className='heading-primary'>Find your place.</h1>
              <div className='homepage__search '>
                <div className='homepage__search__type--select'>
                  <Button
                    className={`homepage__search__type--btn ${
                      this.state.isForSale ? 'is_active' : null
                    }`}
                    onClick={this.toogleListingType('forSale')}
                  >
                    Buy
                  </Button>
                  <Button
                    className={`homepage__search__type--btn ${
                      !this.state.isForSale ? 'is_active' : null
                    }`}
                    onClick={this.toogleListingType('forRent')}
                  >
                    Rent
                  </Button>
                </div>
                <div className='homepage__search--input-box'>
                  <input
                    className='homepage__search--input'
                    type='text'
                    placeholder='City, Neighborhood, Address, School, ZIP, Agent, MLS #'
                  ></input>
                  <button className='homepage__search--input-btn is_active'>
                    <i class='fas fa-search'></i>
                  </button>
                </div>
              </div>

              {/* <p className='paragraph u-margin-bottom-big'>
              Delicious food, consistency and afforable price, are the reasons
              why our customers will return for more
            </p>
            <a
              className='btn btn__order-online'
              href='https://www.doordash.com/store/edo-hibachi-columbia-399441'
            >
              Order Online
            </a> */}
            </div>
            {/* End of header Content  */}
          </div>
          {/* End of header  */}
        </div>
      </header>
    );
  }
}

export default Header;
