import React, { Component } from 'react';
import Map from '../../components/map';
import SearchBar from '../../components/searchBar';
import Nav from '../../components/UI/navbar';
import Listings from '../../components/listings';
import data from '../../components/data/dummy_data';

class Search extends Component {
  state = { data: data, searchText: '' };

  filterData = kw => {
    this.setState({ searchText: kw });
    console.log('previous data ', this.state.data);
    console.log('search KW here: ', kw);

    if (kw.length) {
      const newData = this.state.data.filter(l => {
        let searchText = kw.toLowerCase();

        let doesIdMatches = l.id.toString().match(searchText);

        let neighborhood = l.neighborhood.toLowerCase();
        let doesNeighborhoodMatches = neighborhood.match(searchText);

        let address = l.address.toLowerCase();
        let doesAddressMatches = address.match(searchText);

        console.log('filter :', doesAddressMatches);
        if (
          doesIdMatches !== null ||
          doesNeighborhoodMatches !== null ||
          doesAddressMatches !== null
        ) {
          return true;
        } else {
          return false;
        }
      });
      console.log('new state data ', newData);
      this.setState({ data: newData });
    } else {
      // this.setState({ data: data });
    }
  };
  render() {
    return (
      <div className='searchpage'>
        <p> {this.props.searched_input} </p>

        <div className='searchpage__nav'>
          <Nav>
            <SearchBar onSearch={this.filterData} />
          </Nav>
        </div>
        <div className='searchpage__content'>
          <Map zoom={14} />
          <div className='listings'>
            <Listings data={this.state.data} />
          </div>
        </div>
      </div>
    );
  }
}

export default Search;
